import React, { useState } from 'react';
import config from '../config';
import './Chat.css';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [debugInfo, setDebugInfo] = useState(null);
  const [isDebugging, setIsDebugging] = useState(false);

  const runDebugCheck = async () => {
    setIsDebugging(true);
    setDebugInfo(null);
    const debugResults = [];

    try {
      // Check backend connectivity
      debugResults.push('üîç ◊ë◊ï◊ì◊ß ◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™...');
      let serverCheck;
      try {
        serverCheck = await fetch(`${config.apiUrl}/chat`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ message: 'test' }),
        });

        if (!serverCheck.ok) {
          throw new Error(`Server responded with status: ${serverCheck.status}`);
        }
        debugResults.push('‚úÖ ◊ó◊ô◊ë◊ï◊® ◊ú◊©◊®◊™ ◊™◊ß◊ô◊ü');
      } catch (serverError) {
        if (serverError.message.includes('Failed to fetch')) {
          debugResults.push('‚ùå ◊ú◊ê ◊†◊ô◊™◊ü ◊ú◊î◊™◊ó◊ë◊® ◊ú◊©◊®◊™');
          debugResults.push('◊°◊ô◊ë◊ï◊™ ◊ê◊§◊©◊®◊ô◊ï◊™:');
          debugResults.push(`1. ◊î◊©◊®◊™ ◊ê◊ô◊†◊ï ◊§◊¢◊ô◊ú (${config.apiUrl})`);
          debugResults.push('2. ◊ë◊¢◊ô◊ô◊™ ◊ó◊ô◊ë◊ï◊® ◊ú◊ê◊ô◊†◊ò◊®◊†◊ò');
          debugResults.push('3. ◊ó◊°◊ô◊û◊™ CORS');
          throw serverError;
        }
        throw serverError;
      }

      // Test OpenAI API
      try {
        const apiResponse = await serverCheck.json();
        if (apiResponse.message) {
          debugResults.push('‚úÖ OpenAI API ◊û◊ó◊ï◊ë◊® ◊ï◊§◊ï◊¢◊ú');
        } else if (apiResponse.error) {
          debugResults.push('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊™◊í◊ï◊ë◊™ OpenAI API');
          debugResults.push(`◊§◊ô◊®◊ï◊ò: ${apiResponse.error}`);
        }
      } catch (apiError) {
        debugResults.push('‚ùå ◊©◊í◊ô◊ê◊î ◊ë◊™◊ß◊©◊ï◊®◊™ ◊¢◊ù OpenAI API');
        debugResults.push('◊†◊ê ◊ú◊ï◊ï◊ì◊ê ◊©◊û◊§◊™◊ó ◊î-API ◊™◊ß◊ô◊ü ◊ï◊û◊ï◊í◊ì◊® ◊õ◊®◊ê◊ï◊ô');
      }

      // Environment info
      debugResults.push('\n◊û◊ô◊ì◊¢ ◊°◊ë◊ô◊ë◊î:');
      debugResults.push(`üåê ◊õ◊™◊ï◊ë◊™ ◊©◊®◊™: ${config.apiUrl}`);
      debugResults.push(`üîß ◊û◊¶◊ë: ${process.env.NODE_ENV || 'development'}`);

    } catch (error) {
      console.error('Debug Error:', error);
      if (!debugResults.some(result => result.includes('‚ùå'))) {
        debugResults.push(`‚ùå ◊©◊í◊ô◊ê◊î: ${error.message}`);
      }

      debugResults.push('\n◊¶◊¢◊ì◊ô◊ù ◊û◊ï◊û◊ú◊¶◊ô◊ù ◊ú◊§◊™◊®◊ï◊ü:');
      debugResults.push('1. ◊ï◊ì◊ê ◊©◊î◊©◊®◊™ ◊§◊ï◊¢◊ú ◊¢◊ú ◊§◊ï◊®◊ò 5001');
      debugResults.push('2. ◊ë◊ì◊ï◊ß ◊ê◊™ ◊ß◊ï◊ë◊• .env ◊¢◊ë◊ï◊® OPENAI_API_KEY');
      debugResults.push('3. ◊†◊°◊î ◊ú◊®◊¢◊†◊ü ◊ê◊™ ◊î◊ì◊£');
      debugResults.push('4. ◊ë◊ì◊ï◊ß ◊ê◊™ ◊î-console ◊©◊ú ◊î◊ì◊§◊ì◊§◊ü ◊ú◊©◊í◊ô◊ê◊ï◊™ ◊†◊ï◊°◊§◊ï◊™');
    } finally {
      setDebugInfo(debugResults.join('\n'));
      setIsDebugging(false);
    }
  };

  const sendMessage = async () => {
    if (input.trim() === '') return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(`${config.apiUrl}/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: input }),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      const botMessage = { role: 'bot', content: data.message };
      setMessages(prev => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      setError('◊©◊í◊ô◊ê◊î ◊ë◊©◊ú◊ô◊ó◊™ ◊î◊î◊ï◊ì◊¢◊î. ◊ê◊†◊ê ◊†◊°◊î ◊©◊ï◊ë.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages-container">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`message ${msg.role === 'user' ? 'user-message' : 'bot-message'}`}
          >
            <strong>{msg.role === 'user' ? '◊ê◊™◊î' : '◊†◊¶◊ô◊í ◊©◊ô◊®◊ï◊™'}:</strong> {msg.content}
          </div>
        ))}
        {isLoading && (
          <div className="message bot-message">
            <div className="loading-indicator">◊†◊¶◊ô◊í ◊î◊©◊ô◊®◊ï◊™ ◊û◊ß◊ú◊ô◊ì...</div>
          </div>
        )}
        {error && (
          <div className="error-message">{error}</div>
        )}
        {debugInfo && (
          <div className="debug-info" style={{
            margin: '10px',
            padding: '10px',
            backgroundColor: '#f5f5f5',
            border: '1px solid #ddd',
            borderRadius: '4px',
            whiteSpace: 'pre-line',
            textAlign: 'left',
            direction: 'ltr'
          }}>
            {debugInfo}
          </div>
        )}
      </div>
      <div className="input-container">
        <input
          type="text"
          value={input}
          className="message-input"
          placeholder="◊î◊ß◊ú◊ì ◊ê◊™ ◊î◊ï◊ì◊¢◊™◊ö ◊õ◊ê◊ü..."
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && !isLoading && input.trim() && sendMessage()}
          disabled={isLoading}
        />
        <button 
          onClick={sendMessage} 
          disabled={isLoading || !input.trim()}
          className="send-button"
        >
          ◊©◊ú◊ó
        </button>
        <button 
          onClick={runDebugCheck}
          disabled={isDebugging}
          className="debug-button"
        >
          {isDebugging ? '◊ë◊ï◊ì◊ß...' : '◊ë◊ì◊ô◊ß◊™ ◊û◊¢◊®◊õ◊™'}
        </button>
      </div>
    </div>
  );
}

export default Chat;
